  dmd.setPixel(x + 9, y + 3, GRAPHICS_ON);
  dmd.setPixel(x1 + 6, y1 + 3, GRAPHICS_ON);
  dmd.setPixel(x1 + 9, y1 + 3, GRAPHICS_ON);
  delay(delay_cup_empty_vapour);

  //fourth
  dmd.setPixel(x + 5, y + 2, GRAPHICS_ON);
  dmd.setPixel(x + 8, y + 2, GRAPHICS_ON);
  dmd.setPixel(x1 + 5, y1 + 2, GRAPHICS_ON);
  dmd.setPixel(x1 + 8, y1 + 2, GRAPHICS_ON);
  delay(delay_cup_empty_vapour);
